        this.numberOfParticipants = 0;
      } else if (this.numberOfParticipants > 9) {
        this.numberOfParticipants = 8;
      }
      this.number = data.participants;
    }).catch(error => {
      console.error('Erreur lors de la récupération de l\'activité:', error);
    });
  }
